$(function() {
  var dropdown, slideup;
  if (!$('html').hasClass("touch")) {
    dropdown = function() {
      return $(this).find('div').slideDown('fast');
    };
    slideup = function() {
      return $(this).find('div').slideUp('fast');
    };
    $('#ServicesAndPricing, #Images').on("mouseenter", dropdown).on("mouseleave", slideup);
  }
  return $('#Slideshow').slidesjs({
    width: 800,
    height: 600,
    play: {
      active: false,
      auto: true,
      effect: "fade"
    },
    pagination: {
      active: false,
      effect: "fade"
    },
    navigation: {
      active: false,
      effect: "fade"
    }
  });
});
