$(function() {
  var dropdown, slideup;
  if (!$('html').hasClass("touch")) {
    dropdown = function() {
      return $(this).find('div').slideDown('fast');
    };
    slideup = function() {
      return $(this).find('div').slideUp('fast');
    };
    return $('#ServicesAndPricing, #Images').on("mouseenter", dropdown).on("mouseleave", slideup);
  }
});
